import React from 'react';
import styles from './about.module.css'
import { useState } from 'react';

const About = () => {
    const [location, setLocation] = useState('');
    const [uvData, setUvData] = useState(null);
    //const [reminder, setReminder] = useState(false);

    const fetchUVIndex = async () => {
        if (!location) {
            alert("Please enter your city (e.g., Melbourne)");
            return;
        }

        try {
            const response = await fetch(`http://127.0.0.1:5000/api/uv?location=${location}`);
            const data = await response.json();
            setUvData(data);
        } catch (error) {
            console.error("Error fetching UV index:", error);
        }
    };

    const getUVColor = (index) => {
        if (index <= 2) return 'green';
        if (index <= 5) return 'yellow';
        if (index <= 7) return 'orange';
        if (index <= 10) return 'red';
        return 'purple';
    };

    return (
        <main>
            <section className={styles.infoSection}>
                <h1 className={styles.title}>What do we do ?</h1>
                <p className={styles.desc}>
                At Uv Protection,We are a team focused on the collection and analysis of
                ultraviolet index in Australia, committed to providing users with accurate 
                UV intensity information and scientific outdoor skin protection recommendations. 
                By monitoring UV data across Australia in real time, we help users understand current 
                UV intensity levels and provide personalised sun protection guidelines.
                We are well aware of the potential damage of UV rays to skin health, 
                so we hope to help every user better protect themselves and enjoy
                outdoor activities while protecting themselves from UV
                rays through professional data and practical advice.
                </p>
            </section>

            <section className={styles.uvSection}>
                <h2>Know the UV index in your location</h2>
                <input 
                    type="text"
                    placeholder="Please input your location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                />
                <button onClick={fetchUVIndex}>Check UV Index</button>
                {uvData && (
                <div className={styles.uvResult} style={{ borderColor: getUVColor(uvData.uv_index) }}>
                    <p><strong>{uvData.location}</strong></p>
                    <p style={{ color: getUVColor(uvData.uv_index) }}>UV Index: {uvData.uv_index}</p>
                    <p>{uvData.advice}</p>
                </div>
                )}
                <label>
                    <input
                        type="checkbox"
                        checked={reminder}
                        onChange={() => setReminder(!reminder)}
                    />
                Enable sunscreen reminders
                </label>
            </section>
        </main>
    );
};

export default About;
